@media all {
.ace-tm .ace_gutter {
    background: #262831;
    color: #4b5364;
}
.ace-tm {
    background-color: #313440;
    color: #dedede;
}
.ace-tm .ace_storage, .ace-tm .ace_keyword {
    color: #61afef;
}
.ace-tm .ace_string {
    color: #98c379;
}
.ace-tm .ace_constant.ace_numeric {
    color: #c678dd;
}
.ace-tm .ace_support.ace_function {
    color: #c69a66;
}
.ui-widget-content {
    border: 0px solid #ddd;
    background: #262831;
}
.ace_search_field {
    color: #000;
}
.ace_identifier {
    color: #dedede;
}
.ace-tm .ace_keyword.ace_operator {
    color: #f37269;
}
.ace-tm .ace_constant.ace_language {
    color: #c678dd;
}
}
