css:
@-moz-document regexp(".*mariadb.com/kb/.*") {
/* Light Text */
body.mpkb #top-nav ul li a
, body, body.mpkb div#main
, body.mpkb .navbar
, body.mpkb #navbar-bottom .main-menu .nav a
{
    color: #EEE;
}

/* Colored Text */
.creole code
{
    color: #CCF;
}

/* Dark Background */
.breadcrumb
, .well
, div.product h5.product_title
{
    background-color: #222;
}

/* Lighter Background */
body, body.mpkb div#main
, body.mpkb .navbar
{
    background-color: #333;
}

/* custom / Important */
aside .actions a, aside .ask_button a{
    background-color: #333 !important;
    color: #EEE !important;
}
}
