css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("getpocket.com") {

body {background-color:rgba(0, 0, 0, 0.8) !important;}

#page nav, #page #subnav, #queue_title, header#PKT_header{position:absolute !important}
#page nav{ top:1px !important}
#page #subnav{ top:16px !important}
#page #subnav.show { top:60px !important}
#page_reader[font="serif"] .reader_content,
#page_reader[font="serif"] .reader_head h1 { font-family: Palatino, Georgia !important }
#page_reader[font="sans"] .reader_content,
#page_reader[font="sans"] .reader_head h1 {font-family: Helvetica !important}

.reader_head {margin:0 0 16px !important;padding:0 !important;border-bottom:0 !important}
.reader_head h1, .reader_head .sub {float:left; }
.reader_head .sub{margin:11px 0 0 !important;}
.reader_head h1 { margin:0 .5em 0 0 !important; font-size:24px !important;}
.reader_content  {width:658px !important; font-size:18px !important; line-height:1.5 !important;color:black !important}
.reader_content a {color:rgb(32, 0, 127) !important;text-decoration: none !important}
.text_body p {margin: 1em 0 !important}

.reader_content br {display:block !important; height: 1em !important}
#page_reader { /*background-color:rgba(0, 0, 0, 0.8) !important;*/background-color:transparent !important; padding:0 !important}
.reader_content_wrapper{ position:relative;top:60px; width:800px !important;background-color:white !important;margin:22px auto 0 !important;padding:45px 0 85px !important}

.reader_head .domain,
.reader_head .authors,
.reader_head .date,
.reader_head .tags{display:none !important}
.reader_head .original {background-image:none !important;padding-left:0 !important}
#content .wrapper {border-left: 0 none !important; padding-bottom: 80px !important; border-right:0 none !important}

}
