css:
@-moz-document domain("torrentday.com"), domain("torrentday.me"), domain("torrentday.eu"), domain("torrentday.ru"), domain("td.af") {


/*removes adds*/
 #pageWrapper > div   {display: none !important; }
html body header#banner div#bannerHolder div#popupHolder {display: none !important; }

  /*fixes problems with the dark stylesheet*/
#uiSearch .wrap input {
	width: 100%;
	box-sizing: border-box;
	border: 0;
	background: none;
	font: inherit;
	color: initial ; 
	outline: none;
}

.page_nav a {
	display: inline-block;
	background: initial;
	box-shadow: 0 0 1px #777;
	padding: 2px 7px;
	margin: 0 2px;
	text-decoration: none;
}

/*fixes the background color so you can see text on torrent pages*/
.sect {
	background-color: #363636;
}

 /*fixes the table problem*/
table
  {
    min-width: 500px;
    margin-left: auto;
    margin-right: auto;
     }

}
