css:
@-moz-document url-prefix("https://mangadex.org/"), url-prefix("https://mangadex.org/chapter/") {
body {
    /*color: none;*/
    background-color: #4694AF;
}
.bg-dark {
    background-color: #1C6787;
    background-image: none;
}
.border-bottom {
    border-bottom: 1px solid #004C57;
}
.card {
    background-color: #0088AF;
    border: 1px solid #004C57;
}
.list-group-item {
    background-color: #0088AF;
    border: 1px solid #004C57;
}
body {
    color: #fff;
}
.card-header {
    color: #fff;
    background-color: #2C7AA1;
    background-image: none;
    border-bottom: 1px solid #004C57;
}
.nav-pills .nav-link.active, .nav-pills .show > .nav-link {
    color: #fff;
    background-color: #749FAE;
    background-image: none;
}
a {
    color: #fff;
    text-decoration: none;
}
.navbar-dark .navbar-nav .nav-link {
    color: #fff;
}
.form-control, .form-control:focus, .code-box {
    color: #fff;
    background-color: #4694AF;
}
.form-control {
    color: #ccc;
    background-color: #333;
    background-image: none;
    border: 1px solid #004C57;
}
.form-control::placeholder {
    color: #fff;
    opacity: 1;
}
.btn-secondary:hover, .btn-secondary:focus, .btn-secondary.focus, .btn-light:hover, .btn-light:focus, .btn-light.focus {
    color: #fff;
    background-color: #749FAE;
    background-image: none;
    border-color: #004C57;
}
.btn-secondary, .btn-light {
    color: #fff;
    background-color: #749FAE;
    background-image: none;
    background-repeat: repeat-x;
    border-color: #004C57;
}
.form-control {
    color: #fff;
    background-color: #4694AF;
    background-image: none;
    border: 1px solid #004C57;
}
.dropdown-menu {
    background-color: #0088AF;
    border: 1px solid #004C57;
}
.dropdown-item {
    color: #fff;
}
#reader-controls-collapser {
    border-left: 1px solid #004C57;
    border-right: 1px solid #004C57;
}
.bg-reader-controls {
    background-color: #0088AF;
}
}
