css:
@-moz-document domain("perlmonks.org"), domain("www.perlmonks.org") {


body {
    background-color: darkgrey !Important;
}

.notes, #replies_table, .reply-body {
    background-color: darkgrey !Important;
}

.code, .codeblock, .reply, .nodelet_container, #nodelet_container .nodelet div.link-back { 
    background-color: grey;
    padding : 3pt !Important;
}

.codetext { 
    color: #000;
    padding : 30pt !Important;
}

.user-settings textarea[name="setfreenodelet"]
    {
        font-size : medium !Important;
      font-family :
        "anonymous pro", "dejavu sans mono",  monospace !Important;
            color : #FFF !Important;
            width : 100% !Important;
    }
  /* This deals with the general (user-created) content at perlmonks */
  /* See also node "Line Numbering in Code Listings" [942840] by [Xiong] */


}
