css:
@-moz-document domain("moodle.abtech.edu") {
/* ABTECH BRAND COLORS */
.maroon {
    color: #611a23;
}
.red {
    color: #79222d !important;
}
.blue {
    color: #6393a8;
}
.linkblue {
    color: #4c91b3;
}
.ltgrey {
    color: #f5f4ef;
}
/* STYLES */
#page-my-index {
    background-color: #f5f4ef;
}
a,
.block .block-cards a.coursename,  
.block .block-cards .btn-link {
    color: #1177d1;
    text-decoration: underline;
    transition:color .2s ease-in-out;
}
a:hover,
.block .block-cards a.coursename:hover, 
.block .block-cards .btn-link:hover {
    color: #79222d;
    text-decoration: none;
}
ol > ol, ul > ul, ol > ul, ul > ol {
    margin-bottom: 0;
}
ol, ul, dl {
    margin-left: 30px;
    padding: 0;
}
ul.list-group{
margin-left: 0px;
}
nav.navbar {
    background-color: #611a23 !important;
}
nav.navbar a.navbar-brand,
nav.navbar a.dropdown-toggle {
    color: white !important ;
}
nav.navbar .usermenu .login {
    color:white;
}
nav.navbar .usermenu .login a {
    color: #f0e5b6 !important;
    font-weight:bold;
    padding:0 5px;
}
nav.navbar .usermenu .login a:hover {
    color: #fff !important;
}
nav.navbar .dropdown-menu a {
    color: #79222d !important;
}
nav.navbar .dropdown-menu a:hover {
    color: #6393a8 !important;
    text-decoration: underline;
}
nav.navbar .navbar-nav .nav-link {
    color: rgba(255, 255, 255, .8);
}
nav.navbar .navbar-nav .nav-link:hover {
    color: rgba(255, 255, 255, .5);
}
nav.navbar #action-menu-1-menubar {
    padding: 0 10px;
}
nav.navbar #action-menu-1-menubar .usertext {
    text-decoration: underline;
}
nav.navbar #action-menu-1-menubar img.userpicture {
    margin-right: 0rem;
}
nav.navbar #action-menu-1-menubar:hover {
    padding: 0 10px;
    cursor: pointer;
}
nav.navbar .dropdown a .icon {
    color: rgba(0, 0, 0, .6)!important;
}
nav.navbar .btn-secondary {
    background-color: #6393a8 !important;
    color: white !important;
}
.popover-region-messages .content-item-body{
    /*color:#000 !important;*/
}
.breadcrumb {
    padding: 0;
    margin: 0;
}
.course-content .current::before {
    border-left: #8bd0ee 8px solid;
}
/* NAV DRAWER LEFT */
[data-region="drawer"] {
    background-color: #c2c2c2;
    padding: 16px;
}
#nav-drawer .list-group{
    color: black;
    background-color: #828282;
}
#nav-drawer .list-group:first-child {
    background-color: #6393a8;
}
#nav-drawer .list-group a {
    color: white !important;
    background-color: transparent;
    font-size: .9rem;
    transition:background-color .2s ease-in-out;
}
#nav-drawer .list-group a.list-group-item:hover,
#nav-drawer .list-group a.list-group-item.active {
    background-color: rgba(0, 0, 0, .2);
}
/* COURSEBOXES */
.coursebox > .info > h3.coursename a {
    color: #6393a8 !important;
}
.coursebox > .info > h3.coursename a:hover {
    color: #79222d !important;
}
.card.courses-view-course-item {
    background: #fff;
    box-shadow: 0 0 20px #0000001c;
}
/* CONTENT */
#page-header button {
    background-color:red;
    color:white;
}
#page-header button:hover {
    background-color:#ba0000;
    color:white;
}
.card-body{
    background-color: white;
}
.course-content ul.ctopics li.section .content .toggle,
.course-content ul.ctopics li.section .content.sectionhidden {
    background-color: rgb(0, 0, 0, .1);
}
.course-content ul.ctopics li.section .content div.toggle:hover {
    background-color: rgba(0, 0, 0, .2);
}
.course-content ul.ctopics li.section .content div.toggle:hover h3.sectionname {
    color: #79222d;
}
/* EDITING */
.editing .section .activity .mod-indent-outer {
    padding-left: 1.3rem;
    padding-right: 6rem;
}
.editing .section .activity .contentwithoutlink, 
.editing .section .activity .activityinstance {
    padding-right: 0px !important;
}
body.editing #page a.dropdown-toggle {
    color: red;
}
body.editing #page a.dropdown-toggle:hover {
    color: #437084;
    cursor: pointer;
}
/* UTILITIES */
.section .activity .availabilityinfo,
.section .activity .contentafterlink {
    display: inline;
    margin: 0;
    padding:0px 4px;
}
.tag-info, .badge-info {
    background-color: rgba(255, 255, 255, 0);
    color: #f00;
    font-style: italic;
    padding:0;
    margin-left:11px
}
/* expand course boxes */
.coursebox .content .teachers, 
.coursebox .content .courseimage, 
.coursebox .content .coursefile {
    width: 30%;
}
.coursebox .content .summary, 
.coursebox .content .coursecat {
    float: right;
    width: 65%;
}
/* FULL-SCREEN EDITING AND GRADING */
/* FULL-SCREEN EDITING AND GRADING */
body.path-course-edit .has-blocks,
body.path-mod .has-blocks,
body.path-grade .has-blocks {
    display: inline-block;
    width: calc(100%) !important;
}
body.path-course-edit [data-region="blocks-column"],
body.path-mod [data-region="blocks-column"],
body.path-grade [data-region="blocks-column"] {
    display: none;
}
body#page-mod-assign-grading textarea {
    padding: 5px;
    min-width: 200px;
    max-width: 350px;
    min-height:110px;
    resize: both;
    font-size:.9rem;
}
body#page-mod-assign-grading textarea:focus {
     border: red 2px solid;
}
.path-mod-assign .quickgrademodified textarea,
.path-mod-assign .quickgrademodified input
{
    border: red 2px solid;
    background-color: #ffefef;
}
body#page-mod-assign-grading .comment-message .text_to_html {
    overflow-wrap: break-word;
}
div#id_introeditoreditable {
    min-height: 300px !important;
}
body#page-mod-assign-grading td.submissionstatus,
body#page-mod-assign-grading div.submissionstatus,
body#page-mod-assign-grading a:link.submissionstatus {
    color: black;
    background-color: #dad6c3;
    padding: 2px;
}
.path-mod-assign td.submissionstatussubmitted,
.path-mod-assign div.submissionstatussubmitted,
.path-mod-assign a:link.submissionstatussubmitted {
    color: black;
    background-color: #c4e6c4;
    padding: 2px;
}
.path-mod-assign .lockedsubmission {
    background-color: #ffcfcf;
    font-size: .9rem;
}
.path-mod-assign .comment-list li {
    background: #c4e6c4 !important;
    color: black !important;
}
/* ADDED JAN 2020 */
.section .label .contentwithoutlink, .section .label .activityinstance {
    padding-right: 0;
}
.section .label .mod-indent-outer {
    padding-left: 0px;
}
/* TABLES */
.table-hover tbody tr:hover,
table.grading-report tbody tr:hover,
.forumheaderlist tbody tr:hover,
.generaltable tbody tr:hover,
table.flexible tbody tr:hover,
.category_subcategories tbody tr:hover,
table#modules tbody tr:hover,
table#permissions tbody tr:hover,
.path-mod-assign .gradingtable tr.selectedrow td {
    background-color: #cde0e6;
}
body#page-mod-assign-grading .generaltable thead {
    background-color: #ebebeb;
}
body#page-mod-assign-grading .gradingtable tr.unselectedrow td {
    background-color: #f5f4ef;
}
.table th,
table.collection th,
table.flexible th,
.generaltable th,
.table td,
table.collection td,
table.flexible td,
.generaltable td {
    padding: .25rem;
    vertical-align: top;
    border-top: 4px solid #ffffff;
}
/* SCHEDULE TABLES */
table.dme-schedule {
  background-color: #FEFEFE;
  width: 100%;
  text-align: left;
  border-collapse: collapse;
}
table.dme-schedule td, table.dme-schedule th {
  border: 1px solid #AAAAAA;
  padding: 4px 4px;
}
table.dme-schedule tbody td {
    font-size: .9rem;
    line-height: 1.3;
    padding: 4px;
}
table.dme-schedule tr:nth-child(even) {
  background: #eff4fa;
}
table.dme-schedule thead {
  background: #752424;
  border-bottom: 2px solid #444444;
}
table.dme-schedule thead th {
  font-size: 1.1rem;
  font-weight: bold;
  color: #FFFFFF;
}
table.dme-schedule th {
    border: 1px solid #AAAAAA;
    padding: 4px 4px;
    background: #31708f;
    color: white;
}

/* added 2020FA */
[data-region="blocks-column"] {
    width: 250px;
    float: right;
}
@media (min-width: 1200px) {
#region-main-settings-menu.has-blocks, #region-main.has-blocks {
    width: calc(100% - 265px);
}
}
@media (max-width: 1199px) {
#region-main-settings-menu.has-blocks, #region-main.has-blocks {
    width: calc(100%);
}
}
.editing .section .activity .mod-indent-outer {
    padding-left: 1.3rem;
    padding-right: 0rem;
}
.availabilityinfo.ishidden {
    margin: -10px 0 10px;
}
li.section.main>div.right.side {
    background: #f3f0f0;
    margin-top: 0px;
    margin-right:10px;
}
div#fitem_id_introeditor .col-md-3,
div#fitem_id_page .col-md-3
 {
    flex: 0 0 100%;
    max-width: 100%;
}
div#fitem_id_introeditor .col-md-9,
div#fitem_id_page .col-md-9 {
    flex: 0 0 100%;
    max-width: 100%;
}
td p {
    margin: 3px 0 3px 0;
}
li#section-0 {
    background-color: #f5f4ef;
    padding: 20px 10px 10px 10px;
    border: 1px solid maroon;
    margin: 0px;
}
.course-content .section-summary .summarytext {
    margin: 0px 12px 0px 13px;
}
.summary ol, .summmary ul {
    margin-bottom: 1rem;
}
ul.dragdrop-keyboard-drag {
    margin-left: 0px;
}
ul.dragdrop-keyboard-drag li {
    height: 24px;
    overflow: hidden;
}
ul.dragdrop-keyboard-drag li [data-drop-target*="yui"] {
    color:red;
    font-weight:600;
}
ul.dragdrop-keyboard-drag li [data-drop-target*="module"] {
    opacity:.8;
    padding-left:10px;
}
ul.dragdrop-keyboard-drag li [data-drop-target*="module"]:hover {
    opacity:1;
}
h3.sectionname span.inplaceeditable-text {
    margin-left: 0px;
}    
.course-content ul.topics li.section, .course-content ul.weeks li.section {
    margin-top: 1rem;
    padding-bottom: 1rem;
    padding: .5rem 0;
    border: 1px solid grey;
    box-shadow: 0 0 10px #00000021;
}    
.section_add_menus {
    margin-right: 10px;
}
.course-content ul.topics li.section .summary, .course-content ul.weeks li.section .summary, .course-content ul.topics li.section .content>.availabilityinfo, .course-content ul.weeks li.section .content>.availabilityinfo {
    margin: 0 .5rem;
}

.course-content ul.topics li.section .summary {
	margin: 1rem 2rem 0;
}
.hide, .hidden {
display:none !important;
}
b, strong {
    font-weight: bold !important;
}
pre {
    background: #2d2d2d;
    padding: 10px;
    margin: 1rem;
    color: #fff;
    letter-spacing: 0;
}
pre em, pre i {
    color: #828282;
}
pre b, pre strong {
    font-weight: 700;
    font-size: 100%;
    color: #6dbee1;
} 
pre i b,
pre em strong
{
    font-weight: 700;
    font-size: 130%;
    color: #6dbee1;
    font-style: normal;
    letter-spacing:-.5px;
}
.table thead th, table.collection thead th, table.flexible thead th, .generaltable thead th {
    vertical-align: bottom;
    border: 4px solid #ffffff;
    padding-bottom: 5px;
    line-height: 1;
    font-weight: 600;
    letter-spacing: -.5px;
}

}
