css:
@-moz-document domain("expertplay.net") {
body {
    background: linear-gradient(to right, #ffffff, #ffffff);
}
#ipsLayout_contentArea2 {
    background: linear-gradient(to right, #ffffff, #ffffff);
}
.ipsBreadcrumb > ul > li > a {
    color: #000;
}
.ipsWidget {
    position: relative;
    padding: 0;
    background: #ffffff url(https://2.bp.blogspot.com/-6tm9-qZagYo/WGCwDprjnWI/AAAAAAAAF7E/_Wu2rmu9XeII45S0DWSLpuYX0c1A6cOrwCLcB/s1600/px.png) !important;
    color: #000000;
}
.ipsApp .ipsGrid > .ipsGrid_span26 {
    width: 100%;
}
.ipsApp .ipsGrid > .ipsGrid_span3 {
    width: 100%;
}
html[dir="ltr"] .ipsGrid > [class*="ipsGrid_span"] {
    margin-left: 10px;
}
.ipsType_sectionHead2 {
    text-align: left;
}
.ipsType_light2 {
    text-align: left;
}
#ipsLayout_contentArea2 .ipsType_light, #ipsLayout_contentArea2 .ipsDataItem_stats {
    color: #a9a9a9;
}
#ipsLayout_contentArea2 a {
    color: #525252;
}
.ipsWidget a {
    color: #525252;
}
.ipsType_richText {
    color: #000;
}
.cNewestMember a {
    color: #043a8e !important;
}
.ipsWidget .ipsType_light, .ipsPad_half .ipsType_light {
    color: #4a4a4a;
}
.ipsMessage_error {
    background: #eaeaea;
}
}
