css:
@-moz-document regexp("https?:\\/\\/www\\.google(\\.[a-z]{2,3})?\\.[a-z]{2,3}\\/search.*") {
/* make searchform align center */
.srp #searchform {
    display: flex;
    justify-content: center
}
#sej-container {
    display: flex;
    justify-content: center;
    margin: 0 113px;
}
#gb {
    order: 1;
}
#hdtb-msb {
    justify-content: center;
    width: 98%
}
#hdtb-msb .hdtb-mitem:first-child.hdtb-msel {
    margin: 0
}
.ab_tnav_wrp {
    margin: 0
}
#extabar {
    text-align: center
}
#resultStats {
    position: unset
}
/* search result align center */
.big .mw {
    max-width: 1280px;
    margin: auto
}
#rcnt > .col {
    width: 100% !important;
}
#rhscol {
    display:none
}


/* ！！！！！！！！！columns  */

.big .mw,
.s {
    max-width: unset !important;
}

.col {   
    width: 100% !important;
}

#cnt .mw #center_col,
#cnt .mw #foot {
    width: 173% !important;
}     /*重要，代表所以数列能用的宽度，所以超越100%，因为我的电脑显示器分辨率是显示器本身的2.3倍  */

#center_col {
    margin: auto !important;
    padding: inherit !important;
}

/* entries */

.g {
    padding: 0.367em !important;
}  /* 竖列间距 */

.srg {
    display: flex !important;
    flex-wrap: wrap !important;
}

.srg > .g {
    width: calc(100% /6 - 2.333em) !important;
    margin-left: 0.1em !important;
    margin-right: 0.1em !important;
    overflow: hidden !important;
}

.rgsep {
    display: none !important;
}

/* info box */

.vk_c,
.kp-blk {
    margin: auto !important;
}
}
