css:
@-moz-document domain("chiefdelphi.com") {
.d-header {
	height: 3em;
}
.topic-list th, .topic-list td {
	padding: 2px 5px;
}
.wrap {
	max-width: 1920px;
}
#main-outlet {
	padding-top: 3.75em;
}
.topic-list .posters>a {
	visibility: hidden;
}
.show-more {
	width: 240px;
	text-align: right;
}
.has-topics {
	right: 0;
}
.alert.alert-info {
	background-color: #e89312;
}
.list-controls {
	margin-bottom: 0;
}
ol.category-breadcrumb {
	margin: 0 10px 0 0;
}
.list-controls .nav {
	margin-bottom: 0;
}
.list-controls .btn {
	margin-bottom: 0;
}

.online_users_widget {
    visibility: hidden;
    height: 0;
}
}
