css:
@-moz-document domain("www.appinn.com") {
.main-container {
    width:80% !important;
    margin-left:10%;
    margin-right:10%;
}
figure > img {
    max-width: 80% !important;
    padding-left:10% !important;
}
#page > .article {
    width:80% !important;
    padding-right:2% !important;
}
#page > .sidebar {
    width:18% !important;
    padding:0 !important;
}
#catcher, #site-footer, .header-inner {
    display:none;
}
blockquote {
    font-size:18px !important;
}
#header .sticky-navigation-active {
    position:inherit;
    top:0;
}
header > .entry-title {
    font-size:12px;
}
xheader > .entry-title {
    position:fixed;
    top:0;
    left:0;
    padding-left:10%;
    background: #873c45;
    color:#000000;
}
/*
* 以下为主页卡片样式
*/
 article.post-box > .post-img {
    max-height:160px !important;
}
}
