css:
@-moz-document domain(chiefdelphi.com) {
  html {
    background-color: #F1F1F1;
  }

  .topic-body {
    border-top: 1px solid #FF8800;
  }

  .topic-avatar {
    border-top: 1px solid #FF8800;
  }

  blockquote {
    border-left: 5px solid #999999;
    background-color: #DFDFDF;
  }
  
  aside.quote .title {
    border-left: 5px solid #999999;
    background-color: #DFDFDF;
  }
  
  .topic-map {
    background-color: #E0E0E0;
  }
  
  .topic-map .buttons .btn {
    background: #E0E0E0;
  }
  
  .topic-map .link-summary .btn {
    background: #E0E0E0;
  }
  
  .timeline-container .topic-timeline .timeline-handle {
    background-color: #FF8800;
  }
  
  .timeline-container .topic-timeline .timeline-scrollarea {
    border-color: #FF8800;
  }
  
  .btn-primary {
    background: #FF8800;
  }
  
  .timeline-container .topic-timeline .timeline-last-read .progress {
    color: #FF8800;
  }
  
  .names span a {
    color: #000000;
  }
  
  .topic-list td {
    border-top: 1px solid #999999;
  }
}
