css:
@-moz-document domain(moonwalk.cc), domain(previewer.cc)
{
.theme-line header, .theme-solid header, .modal-content, .dropdown-menu, .table-striped>tbody>tr:nth-of-type(odd), body, .sidebar, .navbar-default {
    background-color: #000!important;
    color: #aaa!important;
}

.modal-body, .content-link, .profile {
    color: #aaa!important;
}

.close, .content-link:hover,  a:hover, a, .link-to-news, .link-to-news:visited {
    color: #fff!important;
    opacity: 1!important;
}

.close, .modal-footer, .modal-content, .modal-header, .label-primary, .label-default, .btn-sm, .btn-group-sm>.btn, .btn-default, .form-control, .pagination>li>a, .pagination>li>span, .table>thead>tr>th, .table>thead>tr>td, .table>tbody>tr>th, .table>tbody>tr>td, .table>tfoot>tr>th, .table>tfoot>tr>td, .btn, .sidebar, .navbar-default {
    border: none!important;
    box-shadow: none!important;
    border-radius: 0px!important;
}

.label-default, .pagination>.active>a, .btn-sm, .btn-group-sm>.btn, .label-primary, .btn-default {
    background-color: #333!important;
    color: #aaa!important;
}

.btn-info, .btn-info:hover, .navbar-default .navbar-nav>.open>a, .navbar-default .navbar-nav>.open>a:hover, .navbar-default .navbar-nav>.open>a:focus, .dropdown-menu>li>a:hover, .dropdown-menu>li>a:focus, .nav>li>a:hover, .nav>li>a:focus, .form-control, .pagination>li>a, .pagination>li>span, .table-hover>tbody>tr:hover, .table-striped>tbody>tr:nth-of-type(odd):hover {
    background-color: #111!important;
    color: #aaa!important;
}
}
