css:
@-moz-document domain(kubernetes.io) {
body,
body > header,
.flip-nav header,
#encyclopedia,
#oceanNodes,
#features,
.panel {
  background-color: #303030;
}

#docsToc,
#docsContent code,
#docsContent pre code,
.highlight > pre,
.list-group-item,
.panel-default > .panel-heading,
pre {
  background-color: #444444 !important;
}

.flip-nav ul.global-nav li a,
.open-nav ul.global-nav li a,
#docsToc .title,
#docsContent code,
#docsContent pre code,
#docsContent,
#oceanNodes,
#features,
h1
 {
  color: #ffffff;
}

a {
  color: #3371e3
}

#docsContent thead,
#docsContent tr:nth-child(even) {
  background-color: #444444;
}
}
