css:
@-moz-document domain("pulseroot.ga") {
body {
    background: #131528;
}

ul.navbar {
    background: #000016;
}

a {
    color: #5d5dfc;
}

.dropdown-content {
    background: #010028;
}

form input, input[type=text] {
    background: #000126;
}

form button, button {
    background: #000028;
}

#messages li {
    background: #00011e;
}


#messages li:nth-child(even) {
	background: rgba(0,1,30,0.6)
}

h1 img {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url("https://neet.host/i/q7SkvV.png") no-repeat;
    width: 561px;
    height: 300px;
    margin: 0 auto;
    padding-left: 561px;
    overflow: hidden;
}
}
