css:
@-moz-document domain(klavogonki.ru) {
#speedpanel-canvas,
#speedpanel-back,
#speedpanel-top,
#speedpanel-arrow {
    display: none !important;
}

#speedpanel {
  width: 740px !important;
  height: 90px !important;
  position: relative !important;
}

#speedpanel #errors-label,
#speedpanel #speed-label {
  font:  30px Tahoma !important;
  display: flex !important;
  justify-content: center !important;
  align-items: center !important;
  top: 5px !important;
  position: absolute !important;
  width: 80px !important;
  height: 80px !important;
}

#speedpanel #speed-label {
  border-right: 1px solid #3Ec5FF !important;
  right: 85px !important;
  background: #3d5a79 !important;
  color: #8EC5FF !important;
}

#errors-label {
  right: 0 !important;
}

#speed-label:after,
#errors-label:after {
  font: 10px Tahoma !important;
  height: 80px !important;
  width: 80px !important;
  display: flex !important;
  justify-content: center !important;
  top: 5px !important;
  position: absolute !important;
}

#speedpanel #speed-label:after {
  content: 'СКОРОСТЬ' !important;
}

#speedpanel #errors-label:after {
  content: 'ОШИБКИ' !important;
}
}
