css:
@-moz-document url-prefix("https://is.muni.cz/auth/elearning/test_export_odpovedi_ucitel?") {
#left_menu {
    display: none !important;
}
#sticky_panel .ikony > i {
    display: inline-block !important;
}

#app_content_row {
    max-width: unset;
}

.data1 {
    width: 100%;
}

.data1 tr:nth-child(2) {
    background-color: #EEE;
}

.data1 tr:nth-child(odd) {
    background-color: #F0F5F5;
}

.data1 th {
    max-width: 30em;
    font-size: 90%;
}
.data1 td {
    max-width: 40em;
}

.data1 pre {
    font-family: serif;
    white-space: pre-wrap;
}

}
