css:
@-moz-document domain("www.litecoinblockhalf.com") {
body { background-color: #4d4d4d; color: #FFF}
table { width: 100%}
.table-striped > tbody > tr:nth-child(2n+1) { background-color: #7a7b7b}
.flip-clock-divider .flip-clock-label { color: #FFF !important}
.table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td { border-top: 0px}
.flip-clock-before { background-color: #fff0}
}
