css:
@-moz-document domain("redux.js.org"), domain("react-redux.js.org") {
.navPusher {
	background-color: black;
	color: #c8c8c8;
}

table tr:nth-child(2n) {
   background-color: #323232;
}

.lightBackground {
	background-color: #323232;
}

.lightBackground img {
	filter: invert(100%);
}

.toc .toggleNav .navGroup .navGroupCategoryTitle {
	color: #764abc;
}

.libBlock h2 a {
	color: lightgrey;
}

.post article blockquote {
   color: black;
   background-color: rgba(255,229,100,.8);
   border-left: none;
}

article img {
	opacity: 0.8;
   transition: opacity, 0.3s
}

article img:hover {
	opacity: 1;
}

#docsNav::-webkit-scrollbar {
  width: 8px;
  height: 8px;
  background-color: grey;
}

#docsNav::-webkit-scrollbar-track {
background-color: #141414;
}

#docsNav::-webkit-scrollbar-corner {
  background-color: #141414;
}

#docsNav::-webkit-scrollbar-thumb {
  background-color: #473663
}

#docsNav::-webkit-scrollbar-thumb:hover {
  background-color: #764abc;
}

.onPageNav>.toc-headings {
	border-left: 1px solid #1e1e1e;
}
}
