@-moz-document domain('gamedev.ru') {

div {
    font-size: 14px;
    font-family: Helvetica;
}

h1 {
    font-size: 18px;
    font-family: Helvetica;
}

div.head {
    background-color: #f0f0f0;
}

h3 {
    background-color: #f0f0f0;
    margin-top: 0px;
    margin-bottom: 0px;
    padding-top: 5px;
    padding-bottom: 5px;
    padding-left: 5px;
}

div.row br {
    visibility: hidden;
    display: none;
}

span.row {
    margin: 0px;
    padding: 1px;
}

}
