css:
@-moz-document domain(threesjs.com) {
body, html {
	background: #090909;
	color: #ddde8c;
}

.game .board {
	background: #222111;
}

.game .next {
	background: #222111;
}

.game .new {
	background: #222111;
}

.tile {
	color: #facade;
}

.tile.red {
	background: #c44;
}

.tile.blue {
	background: #36c;
}
.tile.bonus {
	background: #152;
    color: #facade;
    box-shadow: 0 8px #031;
}
.tile.number {
	background: #152;
    color: #facade;
    box-shadow: 0 8px #031;
}
.game .board .row .tile-container {
	background: #0b1003;
    box-shadow: 0 0px black;
}

.game .new {
	box-shadow: 0 0px black;
}
}
