css:
@-moz-document domain("sr.ht") {
body > .container { margin: auto; }
body > .container-fluid { max-width: 1140px; }

.header-tabbed {
  border: none;
}

nav.navbar {
  flex-wrap: wrap;
  margin: auto;
}

@media(min-width: 768px) {
  nav.navbar { max-width: 100%; }
}

@media(min-width: 992px) {
  nav.navbar { max-width: 960px; }
}

@media(min-width: 1200px) {
  nav.navbar { max-width: 1140px; }
}
}
