css:
@-moz-document url("https://www.kvakil.me/venus/") {
:root {
  --black: black;
  --gray4: #444;
  --gray3: #333;
  --gray2: #222;
  --graye: #eee;
  --white: #fff;
  --accent: #00d1b2;
}

body {
  background-color: var(--gray2);
}

.tabs {
  background-color: var(--gray3);
  color: var(--white);
  border-bottom: 0;
}

.tabs ul {
  border-bottom: 1px solid var(--graye);
}

.tabs li {
  background-color: var(--white);
  border: 0;
  border-radius: 0;
}

.tabs.is-boxed li.is-active a,
.tabs li.is-active a {
  background-color: var(--accent);
  color: var(--gray2);
  border: 2px solid;
  border-radius: 0;
}

.section {
  background-color: var(--gray3);
  padding-bottom: 20px;
}

.CodeMirror-gutters {
  background-color: var(--gray2);
}

.CodeMirror-scroll {
  background-color: var(--graye);
}

.field {
  background-color: var(--gray3);
}

.label {
  color: var(--white);
}

.panel-tabs a.is-active {
  color: var(--black);
  background-color: var(--accent);
  border: 1px solid var(--white);
}

a {
  color: var(--white);
}

.panel-block {
  border: 0;
}

.panel-heading:first-child,
.panel-tabs:first-child {
  border: 0;
  border-bottom: 1px solid white;
  background-color: var(--gray2);
}

.table {
  background-color: var(--gray2);
  color: var(--white);
}

#program-listing-body tr.is-selected {
  color: var(--black);
}

.table tr:hover {
  background-color: var(--gray4);
}

.table thead td,
.table thead th {
  color: var(--white);
}

.table.is-striped tbody tr:not(.is-selected):nth-child(even) {
  background-color: var(--gray2);
}

#memory-table tr th {
  background-color: var(--gray2);
}

#memory-table tr {
  background-color: var(--gray2);
}

#memory-table tr:hover {
  background-color: var(--gray4);
}
}
