/* ==UserStyle==
@name           troyhunt.com dark theme
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Dark theme for troyhunt.com. For those of us on the Dark Side.
@author         ivaks
}
==/UserStyle== */
@-moz-document domain("troyhunt.com") {
  body,
  .pagination {
    color: #dfdbd5 !important;
    background-color: #181714 !important;
  }

  .content-sidebar {
    color: #dfdbd5 !important;
    background-color: #23211b !important;
  }

  p,
  th,
  td {
    color: #dfdbd5 !important;
  }

  .header_block {
    position: relative !important;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  .post_title a {
    color: #ccb285 !important;
  }

  .cycle,
  .post_tag a,
  .article-open_tag a,
  .post_socialIcons .socialIcons_link a,
  .article_readNext {
    color: #dfdbd5 !important;
    background-color: #3b3932 !important;
  }
}
