/* ==UserStyle==
@name           scotthelme.co.uk dark theme
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Dark theme for scotthelme.co.uk
@author         ivaks
==/UserStyle== */
@-moz-document domain("scotthelme.co.uk") {
  body,
  .comment-wrap,
  .disqus-container {
    color: #ccc !important;
    background-color: #202020 !important;
  }

  h1,
  h2,
  h3,
  h4,
  h5,
  h6,
  .project-description {
    color: #ccc !important;
  }

  a,
  a:hover {
    color: #64b1ff;
  }

  .main-navigation {
    color: #ccc !important;
    background-color: #202020 !important;
    border: none !important;
  }

  .main-navigation .menu li a {
    color: #ccc !important;
  }

  .post,
  .sidebar .widget {
    color: #ccc !important;
    background-color: #323232 !important;
  }

  .sidebar .widget .title {
    border-bottom-color: #555;
  }

  .sidebar .widget .title::after {
    background: #12569a;
  }

  .sidebar .widget .upcoming-event .event {
    border-bottom-color: #555;
  }

  .event a {
    color: #ccc !important;
  }

  .post .post-head .post-title a {
    color: #ccc !important;
  }

  .support-banner,
  .support-message {
    color: #ccc !important;
    background-color: #323232 !important;
  }
  
  textarea.contact-form {
    color: #ccc;
    background-color: #555;
  }
  
  button#button {
    color: #222 !important;
  }
  
}
