/* ==UserStyle==
@name           securityheaders.com dark theme
@namespace      github.com/openstyles/stylus
@version        1.0.0
@description    Dark theme for securityheaders.com
@author         ivaks
==/UserStyle== */
@-moz-document domain("securityheaders.com") {
  body,
  #main,
  .reportSection {
    color: #ccc;
    background-color: #202020;
  }

  .reportTitle {
    color: #ccc;
    background-color: #323232;
  }

  .tableCell {
    color: #ccc;
  }

  .tableLabel {
    color: #ccc;
  }


  .table_red {
    color: red;
    font-weight: 700
  }

  .table_orange {
    color: orange;
    font-weight: 700
  }

  .table_yellow {
    color: #FFD242;
    font-weight: 700
  }

  .table_green {
    color: #41a832;
    font-weight: 700
  }

  .table_lightgreen {
    color: #4ec83d;
    font-weight: 700
  }

  .table_grey {
    color: grey;
    font-weight: 700
  }

  .table_blue {
    color: #116CFF;
    font-weight: 700
  }


  .reportSection {
    border: none !important;
  }

  a,
  a:hover {
    color: #0b88ee;
  }

  a {
    text-decoration: none;
  }

  input#q {
    color: #ccc !important;
    background-color: #323232!important;
    border: none !important;
  }
}
