css:
/*dark stuff*/
/*dark dark*/
pre, code, html, blockquote, aside, page-standard, i, textarea, input {
  color:#d5d5d5 !important;
  background: #2a2a2a !important;
  background-color: #2a2a2a !important;
  border-color: #d5d5d5 !important;
}
/*light dark*/
body, main, footer, table, div, button, span, h1, h2, h3, TD, TH, nav, select, article, ul, body::before, section, div::after, form, figcaption, figure{
  color:#d5d5d5 !important;
  background: #2f2f2f !important;
  background-color: #2f2f2f !important;
  border-color: #d5d5d5 !important;
}
/*Permissive object test section. Move up if these fail.*/
header {
  color:#d5d5d5;
  background: #2f2f2f;
  background-color: #2f2f2f;
  border-color: #d5d5d5;
}
/*text*/
/*links*/
a, a:link, a:hover {
    color: #db4156 !important;
    background: transparent !important;
    border-bottom: none;
    box-shadow: none;
}
a:visited {
    color: #ce0631 !important;
    background: transparent !important;
    border-bottom: none;
    box-shadow: none;
}
/*white text*/
p, span, b, TR, small, li, ol, h4, h5, h6, hr::before, label, strong, em {
  color:#d5d5d5 !important;
  background: transparent !important;
}

/*other*/
/*icon color*/
path {
    stroke: #d5d5d5 !important;
}
