css:
@-moz-document domain("dev.to") {
.stories-show {
    background: #ffffff;
}

.article,
.primary-sticky-nav .primary-sticky-nav-element,
.primary-sticky-nav-author,
.primary-sticky-nav-author-element,
.top-bar {
    border: none !important;
    box-shadow: none !important;
}

.container .article-actions {
    background: #DDE3E9;
    border: none;
    box-shadow: var(--theme-container-box-shadow, 0px 0px 5px rgba(0, 0, 0, 0.25));
}

.nav-search-form__input {
    background: #ffffff;
}

.top-bar {
    background: #DDE3E9;
    position: absolute;
}

.primary-sticky-nav-element *,
.primary-sticky-nav-element-wrapper * {
    color: #2E3841 !important;
}

.single-comment-node .inner-comment {
    border: none;
    border-left: 2px solid #DDE3E9;
}
}
