css:
@-moz-document domain("www.netflix.com") {
body, html {
	background: #000;
}
.profiles-gate-container {
	background: #000;
}
.list-profiles {
	background: #000;
}
.pinning-header-container {
	background: rgb(0, 0, 0) !important;
}
.billboard-row .billboard-originals.full-bleed-billboard .hero-vignette {
	background-image: -webkit-gradient(linear,left top,left bottom,from(rgba(20,20,20,0)),color-stop(15%,rgba(20,20,20,.15)),color-stop(29%,rgba(20,20,20,.35)),color-stop(44%,rgba(20,20,20,.58)),color-stop(68%,#000),to(#000));
	background-image: -webkit-linear-gradient(top,rgba(20,20,20,0) 0,rgba(20,20,20,.15) 15%,rgba(20,20,20,.35) 29%,rgba(20,20,20,.58) 44%,#000 68%,#000 100%);
	background-image: -moz-linear-gradient(top,rgba(0, 0, 0, 0) 0,rgba(0, 0, 0, 0.2) 15%,rgba(0, 0, 0, 0.3) 29%,rgba(20,20,20,.58) 44%,#000 68%,#000 100%) !important;
	background-image: -o-linear-gradient(top,rgba(20,20,20,0) 0,rgba(20,20,20,.15) 15%,rgba(20,20,20,.35) 29%,rgba(20,20,20,.58) 44%,#000 68%,#000 100%);
}
}
