css:
@-moz-document url-prefix("https://www.youtube.com/tv#/") {
   #app-markup {
    background-color: #000;}
    #guide {
    background-color: #000;}
    .top-nav-tab .top-nav-tab-highlight {
    background-color: #333333;}
    .overlay-action-panel.welcome {
	background: #000;
}
    .overlay-partials-menu-item.focused {
	background: #373737;
	color: #fff;
}
    .empty-state-pane.add-account .empty-state-pane-sidebar {
	background-image: none !important;
}
    .empty-state-pane {
	background-image: none !important;
}
    .surface .secondary-nav {
	background-color: #000;
}
    .top-nav-tab.selected.focused {
    color: #e6e6e6;}
    
    #loader {
	filter: contrast(150%);
} 
    .airstream-album-tile .watchable-tile-thumbnail-background, .airstream-playlist-tile .watchable-tile-thumbnail-background, .airstream-video-tile .watchable-tile-thumbnail-background {
	background-color: #000 !important;
}
    .auth-action .toast-container {
	background-color: #000000DB;
}
    .toaster .toast-container {
	background-color: #000000DB;
}
    .auth-action .main-text {
	color: #FFF;
}
}
