css:
@-moz-document domain("forja.tn") {
    body, .modal-header {
        background: #171717 !important;
    }
    h1, h2, h3, h4, h5, h6, p, .forja_media_card2-date, .forja_media_card2-context-label, a, .forja_media_card2-context-label, .fa, #mobile-menu *, input, label {
        color: #ffffff !important;
    }
    .tabs-forja li a:hover {
        border-color: #ffffff !important;
        border-bottom: 3px solid white!important;
        color: #ffffff !important;
    }
    .forja_media_card2:hover {
        box-shadow: #ff7e00 1px 3px 6px 3px;
        opacity: .9;
    }
    .forja_media_card2:hover .forja_media_card2-context, .dropdown-content, .modal-body, footer.page-footer, .slide_click:hover, .forja_media_card2-details, #dafeFoVip, #mobile-menu {
        background: #1d1d1d !important;
    }
}
