css:
@-moz-document url(https://s3.us-east-2.amazonaws.com/cis366-kobert/index.html) {
	@font-face {
		font-family: "Alex-Brush";
		src: url(alex-brush/AlexBrush-Regular.ttf);
	}

	h1 {
		font-family: "Alex-Brush", cursive;
		font-size: 70px;
		text-align: center;
		background-color: LightSkyBlue;
		border: 10px #efefef solid;
	}

	h3 {
		font-family: Arial;
		font-size: 25px;
		text-align: center;
	}

	li {
		list-style: none;
		margin-left: auto;
		margin-right: auto;
		margin-top: 15px;
		padding-top: 15px;
		padding-bottom: 15px;
		text-align: center;
		width: 200px;
		border: #efefef solid 2px;
		border-radius: 15px;
		background-color: LightSkyBlue;
	}
	li a {
		color: black;
		text-decoration: none;
	}

	li a:hover {
		color: #efefef;
	}

	footer {
	  position: absolute;
	  right: 0;
	  bottom: 0;
	  left: 0;
	  padding: 1rem;
	  background-color: #efefef;
	  text-align: center;
	  color: black;
	}

	footer a {
		text-decoration: none;
		padding: 20px;
		color: black;
	}

	footer a:hover {
		color: darkgray;
	}
}
