css:
@-moz-document url-prefix("https://bair.berkeley.edu/blog") {

body {
  color: #aaa;
  background-color: #222;
}

/* Link */
a {
  color: #999;
}

a:hover {
  color: #0076df;
}

/* Image */
img {
  opacity: 0.5;
}

/* Video */
.html5-video-player {
  opacity: 0.5;
}

.videoWrapper {
  opacity: 0.5;
}

/* Headers - 'Subscribe', 'About', 'Archive', 'BAIR' */
.site-header nav a {
  color: #aaa;
}


/*** Footer ***/
footer {
  background-color: #282828;
  border-top-color: #333;
  color: #aaa;
}


/***** Main Page - bair.berkeley.edu/blog/ *****/

p {
  color: #aaa;
}

.posts .post {
  border-bottom-color: #333;
}



/* Quote */
blockquote {
  background-color: #282828;
  border-left-color: #333;
}

/* 'Newer', 'Older' */
.pagination a, .pagination .disabled {
  background-color: #282828;
  border-color: #333;
  color: #aaa;
}

.pagination a:hover, .pagination a:focus {
  background-color: #444;
  color: #0076df;
}


/***** Article Page - bair.berkeley.edu/blog/YYYY/MM/DD/<article-name>/ *****/


/* Title */
.post-header {
  color: #999;
}


/* 'Subscribe to our RSS feed' */
.generic-box {
  background-color: #282828;
  border-color: #333;
}

/***** Archive Page - bair.berkeley.edu/blog/archive/ *****/

/** Search **/
.gsc-control-cse {
  background-color: #282828;
  border-color: #333;
}

.gsc-input-box {
  background-color: #222;
  border-color: #333;
}

.gsc-input {
  background-color: #222 !important;
}

/* Search button */
.gsc-search-button-v2, .gsc-search-button-v2:hover, .gsc-search-button-v2:focus {
  background-color: #282828;
  border-color: #282828;
}

td.gsc-search-button {
  border-top-color: #282828;
}

.gsc-search-button-v2 svg {
  fill: #ccc;
}

/* Search dropdown */

.gsc-completion-container {
  background-color: #333;
  border-color: #666;
}


/***** Subscribe Page - bair.berkeley.edu/blog/subscribe/ *****/

#mc_embed_signup {
  background-color: #282828;
}

/* Error Message */
#mc_embed_signup #mc-embedded-subscribe-form div.mce_inline_error {
  background-color: #333;
}

/* Input */
#mc_embed_signup input {
  border-color: #666;
}

/* 'Subscribe' */
#mc_embed_signup .button {
  background-color: #222;
  color: #ccc;
}

/***** About Page - bair.berkeley.edu/blog/about/ *****/

pre, code {
  background-color: #333;
}

}

@-moz-document url-prefix("https://disqus.com/embed/comments/") {

/***** Article Page - bair.berkeley.edu/blog/YYYY/MM/DD/<article-name>/ *****/

.comment-policy {
  color: #aaa;
  background-color: #282828;
}

.comment-policy i {
  color: #666;
}

}
