css:
@-moz-document domain("forum.truckersmp.com") {
@media(min-width: 1280px){
    .ipsLayout_container {
    	max-width: 85%;
    }
}
@media(max-width: 1279px){
    .ipsLayout_container {
    	max-width: 95%;
    }
}

#elLogo img {
    display: none !important;
}

.ipsImage_thumbnailed {
    border: none !important;
}

#elLogo {
	display: block !important;
    -moz-box-sizing: border-box !important;
    -webkit-box-sizing: border-box !important;
    box-sizing: border-box !important;
    background: url(https://truckersmp.com/assets/img/truckersmp-logo-sm.png) no-repeat !important;
	background-size: auto auto !important;
    width: 369px !important;
    height: 80px !important;
    padding-left: 369px !important;
	top: 10px !important;
	position: relative !important;
	margin-bottom: 2% !important;
}

a.ipsButton_overlaid {
    opacity: 0.2 !important;
}

a.ipsButton_overlaid:hover {
    opacity: 0.8 !important;
}

a.ipsButton_overlaid:active {
    opacity: 1 !important;
}

a.ipsButton {
    margin: 0px 2px 0px 2px !important;
}


.ipsStreamItem_contentType {
    display: none !important;
}

.ipsButton_light span {
    background: none !important;
}

.ipsButton_medium span {
    background: none !important;
}

#elProfileStats {
    background: #303030 !important;
}
}
