css:
@-webkit-document url-prefix(http://google.com),
  url-prefix(https://google.com),
  url-prefix(http://www.google.com),
  url-prefix(https://www.google.com) {

	#myuser {
		transform: scale(0.7);
	}

	/* 显示搜索菜单 */
	.hdtb-td-h {
		display: block !important;
	}

	#hdtbMenus {
		top: 80px !important;
	}

	#hdtb-tls {
		display: none !important;
	}

	/* 工具栏移动位置 */
	.hdtb-mn-hd {
		top: -58px !important;
		left: 450px !important;
		z-index: 99999 !important;
	}

	/* 工具栏打开的列表位置与样式*/
	.hdtb-mn-o {
		margin: 10px 0px !important;
		left: 670px !important;
		border-radius: 5px;
		padding: 6px 10px !important;
		box-shadow: -1px 1px 9px 0px #999;
		transition: all 0.8s;
	}

	/* 设置按钮位置调整 */
	#ab_options {
		position: absolute;
		top: 18px;
		right: 223px;
	}
}
