@-moz-document domain("scotgoespop.blogspot.com") {
body {
	color: #ccc;
}
.main-outer, .footer-outer {
	background-color: #2d2d2d;
}
h1, h2, h3, h4, h5, h6 {
	text-shadow: none;
    color: #eee;
}
.postCommentButtonHolder {
	background-color: #2d2d2d;
}
.post-footer {
	color: #fff;
	background-color: #444;
	border-bottom: 1px solid #444;
}
