css:
@-moz-document domain("esperanto.net") {
a {
    color: darkgreen;
    text-decoration: none;
}

a:visited {
    color: chocolate;
}

html,
body,
.vsc-initialized {
    display: flex;
    flex-direction: column;
    align-self: center;
    align-content: center;
    justify-content: center;
    background-color: #222;
    font-family: Constantia, "Times New Roman", serif;
    padding: 0 15vw 0 15vw;
}


font {
    font-family: Constantia, "Times New Roman", serif;
}
p,
td {
    color: azure;
    font-size: 1.2rem;
}

p {
    line-height: 2rem;
}
}
