css:
@-moz-document domain("bertilow.com") {
body,
html,
.komputo,
.main,
header {
    background-color: #222;
}

.sek1 {
    color: azure;
    background-color: #222;
}

html.m main,
html.m .main,
html.m body {
    background-color: #222;
}

body p.kodo,
body pre.kodo,
body p.komandoj,
body pre.komandoj,
footer {
    background-color: #444;
}

html.m main > div > footer[role=contentinfo],
html.m .main > div > .footer[role=contentinfo] {
    background-color: #333;
    color: lightgray
}

body del {
    background-color: #333
}

body ins {
    background-color: #111
}

html.m main,
html.m .main {
    color: azure;
}

body table {
    background-color: #222;
}

body pre.kodo b.SINTAKSO {
    color: green;
}

body pre.kodo b.S_ATRIBUTO {
    color: tomato;
}

body pre.kodo b.S_VALORO {
    color: skyblue;
}

body p.kodo b.SINTAKSO,
body p.kodo b.SINTAKSOSIMPLA,
body pre.kodo b.SINTAKSO,
body pre.kodo b.SINTAKSOSIMPLA,
body p.komandoj b.SINTAKSO,
body p.komandoj b.SINTAKSOSIMPLA,
body pre.komandoj b.SINTAKSO,
body pre.komandoj b.SINTAKSOSIMPLA {
    color: green;
}

body table tr:hover th,
body table tr:hover td {
    background-color: #444
}
}
