css:
@-moz-document domain("literaturo.org") {
a {
    color: darkgreen;
    text-decoration: none;
}

a:visited {
    color: chocolate;
}

body {
    background: #222;
}

h1,
h2,
h3,
h4,
p,
td {
    color: azure;
    font-family: Constantia, "Times New Roman", serif;
    padding: 3px;
    margin: 0;
}

td {
    font-size: 1.2rem;
    padding: 16px;
}
}
