css:
@-moz-document url-prefix("http://notabenoid.org/") {
/* Компактный стиль для нотабеноида 3.0 (автор Nikopol) */
/* уменьшаем отступы у постраничного перехода */

.pagination {
    margin-top: 6px;
    margin-bottom: 2px;
}
/* отступы блоков с вариантами переводов, цвет текста */

.translator td.t > div {
    margin: 6px;
    padding: 4px;
    color: #B4B4B4;
}
/* цвет и толщина финального/лучшего варианта перевода */

.translator td.t > div.best .b {
    font-weight: normal;
}
/* цвет фона финального/лучшего варианта перевода */

.translator td.t > div.best {
    background-color: #f2f3f4;
}
.translator td.n {
    padding: 2px;
}
.translator > tbody > tr > td {
    padding-top: 1px;
    padding-bottom: 1px;
}
.translator td.t {
    padding: 0;
}
/* основной цвет переводов */

.translator td.t > div .b {
    color: black;
}
/* цвет ссылки на профиль переводчика */

.translator td.t > div a.user {
    color:  #1f81bb;
}
/* цвет ссылки на создание нового варианта перевода */

.translator td.u a.t {
    color:  #e8005f;
}

}
