css:
@-moz-document domain("octopi.local") {
body {
    background-color: #212121;
    color: #ffffff;
}

.page-container {
    background-color: #212121;
    color: #bababa;
}

hr {
    border-top: 1px solid #000;
    border-bottom: 1px solid #000;
}

.progress {
    background-image: -moz-linear-gradient(top, #000, #131212);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#000), to(#131212));
    background-image: -webkit-linear-gradient(top, #000, #131212);
    background-image: -o-linear-gradient(top, #000, #131212);
    background-image: linear-gradient(to bottom, #000, #131212);
}

.accordion-group {
    border: 1px solid #000;
}

.btn {
    color: #bababa;
    text-shadow: 0 1px 1px rgba(0, 0, 0, 0.75);
    background-image: -moz-linear-gradient(top, #000, #131212);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#000), to(#131212));
    background-image: -webkit-linear-gradient(top, #000, #131212);
    background-image: -o-linear-gradient(top, #000, #131212);
    background-image: linear-gradient(to bottom, #000, #131212);
    background-color: #e6e6e6;
    border: 1px solid #ccc;
    border-bottom-color: rgb(204, 204, 204);
    border-bottom-color: #b3b3b3;
    -webkit-box-shadow: inset 0 1px 0 rgba(255, 255, 255, .2), 0 1px 2px rgba(0, 0, 0, .05);
    -moz-box-shadow: inset 0 1px 0 rgba(255, 255, 255, .2), 0 1px 2px rgba(0, 0, 0, .05);
    box-shadow: inset 0 1px 0 rgba(255, 255, 255, .2), 0 1px 2px rgba(0, 0, 0, 0.05);
}

.btn.active,
.btn.disabled,
.btn:active,
.btn:focus,
.btn:hover,
.btn[disabled] {
    color: #3c3c3c;
    background-color: #0e0e0e;
}

#files:hover .gcode_files:hover .entry:hover {
    background-color: #252525;
}

textarea,
input[type="text"],
input[type="password"],
input[type="datetime"],
input[type="datetime-local"],
input[type="date"],
input[type="month"],
input[type="time"],
input[type="week"],
input[type="number"],
input[type="email"],
input[type="url"],
input[type="search"],
input[type="tel"],
input[type="color"],
.uneditable-input {
    background-color: #000;
    border-color: #bababa;
}

.nav-tabs {
    border-bottom: 1px solid #000;
}

.nav-tabs > .active > a,
.nav-tabs > .active > a:hover {
    color: #bababa;
    background-color: #212121;
    border: 1px solid #000;
}

.octoprint-container .tab-content {
    border-left: 1px solid #000;
    border-right: 1px solid #000;
    border-bottom: 1px solid #000;
}

.octoprint-container .tab-content h1 {
    color: #bababa;
    border-bottom: 1px solid #000;
}

.navbar-inner {
    border: 1px solid #000;
}

#navbar .navbar-inner.black {
    background-image: -moz-linear-gradient(top, #373737, #121212);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#373737), to(#121212));
    background-image: -webkit-linear-gradient(top, #373737, #121212);
    background-image: -o-linear-gradient(top, #373737, #121212);
    background-image: linear-gradient(to bottom, #373737, #121212);
}
tr:hover {
    background-color: #252525;
}

.table-bordered {
    border: 1px solid #000;
}

.table-bordered:hover {
    background-color: #252525;
}

.table-bordered th,
.table-bordered td {
    border-left: 1px solid #000;
}

.table th,
.table td {
    border-top: 1px solid #000;
}

.input-append .add-on,
.input-prepend .add-on {
    background-color: #000;
    border: 1px solid #ccc;
}

#navbar .navbar-inner.black .nav > li.dropdown.active > .dropdown-toggle,
#navbar .navbar-inner.black .nav > li.dropdown.open.active > .dropdown-toggle,
#navbar .navbar-inner.black .nav > li.dropdown.open > .dropdown-toggle {
    background-image: -moz-linear-gradient(top, #373737, #121212);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#373737), to(#121212));
    background-image: -webkit-linear-gradient(top, #373737, #121212);
    background-image: -o-linear-gradient(top, #373737, #121212);
    background-image: linear-gradient(to bottom, #373737, #121212);
}

.dropdown-menu {
    background-color: #303030;
}

.dropdown-menu li > a {
    color: #fff;
}

.modal-backdrop,
.modal-backdrop.fade.in {
    background: #000;
}

.modal {
    background-color: #212121;
}

legend {
    color: #c5c5c5;
}

.modal-footer {
    background-image: -moz-linear-gradient(top, #212121, #121212);
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#212121), to(#121212));
    background-image: -webkit-linear-gradient(top, #212121, #121212);
    background-image: -o-linear-gradient(top, #212121, #121212);
    background-image: linear-gradient(to bottom, #212121, #121212);
}

.close {
    color: #fff;
    text-shadow: 0 1px 0 #fff;
}
}
