@-moz-document domain("stackexchange.com") {
::-webkit-scrollbar-thumb,
body::-webkit-scrollbar-thumb,
ol::-webkit-scrollbar-thumb,
ul::-webkit-scrollbar-thumb,
#transcript-scrollbox::-webkit-scrollbar-thumb {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to right, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border-radius: 22px!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar-thumb:hover,
body::-webkit-scrollbar-thumb:hover,
ol::-webkit-scrollbar-thumb:hover,
ul::-webkit-scrollbar-thumb:hover,
#transcript-scrollbox::-webkit-scrollbar-thumb:hover {
    /* Color of handle */
    background: #580303!important;
    background-image: linear-gradient(to left, rgba(93, 6, 6, 0.95), rgba(255, 4, 4, 0.1)), url(https://image.ibb.co/hCq4n5/Carbon_Fiber_2.jpg)!important;
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-track,
#messages div::-webkit-scrollbar-track {
    /* Color of slider */
    background: #170000!important;
    box-shadow: inset 8px 0 13px rgba(0, 0, 0, .4), rgba(55, 55, 55, 1) 0 1px 0!important;
    border: 1px solid rgba(0, 0, 0, .25)!important;
}
 
::-webkit-scrollbar,
#messages div::-webkit-scrollbar {
    /* Width */
    width: .8em!important;
}
 
::-webkit-scrollbar-button,
#messages div::-webkit-scrollbar-button {
    /* Color of handles at top and bottom */
    background: #4c0707!important;
    box-shadow: inset 0 12px 13px rgba(0, 0, 0, 0.6), rgba(55, 55, 55, 1) 0 1px 0!important;
}
 
::-webkit-scrollbar-button:hover,
#messages div::-webkit-scrollbar-button:hover {
    border-radius: 22px!important;
    box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
}
}

@-moz-document domain("stackexchange.com") {
/*__________________________________________________________________________________*/

* {
	text-decoration: none!important;
}

#tabs a,
.privileges-page .short-description,
.tabs a,
.topbar-dialog .header h3
{
	text-transform: capitalize!important;
}

#tabs a.youarehere,
#tabs a:hover,
.ba,
.company-tag,
.container .chosen-choices .search-choice,
.container .chosen-container-multi .chosen-choices li.search-choice,
.disliked-tag,
.favorite-tag,
.geo-tag,
.moderator-tag,
.module,
.page-numbers,
.post-tag,
.required-tag,
.s-btn__muted.s-btn__outlined,
.s-sidebarwidget,
.status.answered,
.tabs a.youarehere,
.tabs a:hover,
:focus
{
	border: none!important;
	outline: none!important;
	box-shadow: none!important;
}

path:first-child {
    fill: rgb(255, 3, 3);
}

/*___ Fonts ________________________________________________________________________*/

.comment-copy,
.comment-text,
.excerpt,
.post-text p,
.post-text,
.question-hyperlink,
.question-status,
.top-bar,
.topbar-dialog .header h3,
.topbar-dialog a,
.topbar-dialog.network-logo-dialog h4,
.wmd-preview,
body,
p
{
	font-family: cursive, Roboto, Arial, sans-serif!important;
}

.mb24 {
	font-size: 21px!important;
}

.heading,
.module h3,
.module.community-bulletin .bulletin-title,
.pln,
.post-tag,
.post-text code,
.post-text p,
.page-description,
.pun,
.question-hyperlink,
.subheader h1,
.summary h3,
.topbar-dialog.network-logo-dialog h4,
.users-page .user-info .user-details>a
{
	font-size: 19px!important;
}

.badge,
.badge-tag,
.badges-page .badge-row,
.badges-page .mb,
.comment-copy,
.excerpt,
.faq-page .col-section,
.featured-site .site-details a,
.hero-box em,
.post-text blockquote p,
.privileges-page #privilege-table,
.topbar-dialog .header h3,
.wmd-preview code
{
	font-size: 17px!important;
}

#hot-network-questions a,
#tags_list .excerpt,
.comment-text,
.featured-site .site-details,
.help-center .help-category-tree li a,
.item-multiplier-count,
.s-sidebarwidget--content,
.topbar-dialog .modal-content,
.users-sidebar .userDetails .sb-userName
{
	font-size: 15px!important;
}

.metaInfo,
.users-page .user-info .user-details .user-location,
.users-page .user-info .user-tags,
.users-sidebar .userDetails .sb-siteName
{
	font-size: 14px!important;
	line-height: 19px!important;
}

#tags_list .excerpt,
.help-center .help-category-tree li a,
.page-description
{
	line-height: 24px!important;
}

/*___ Remove Items _________________________________________________________________*/

#tabs a.youarehere:before,
.message.message-info .message-tip-left-top:before,
.tabs a.youarehere:before
{
	left: -9999px!important;
	position: absolute!important;
	top: -9999px!important;
}

/*___ Left/Right/Top/Bottom ________________________________________________________*/

.flush-left {
    margin-left: -11px!important;
}

/*___ Margins ______________________________________________________________________*/

#answers,
#mainArea,
#mainbar,
#sideBar,
#sidebar,
#tags_list .tag-cell,
.grid-layout>.grid-layout--cell,
.left-sidebar,
.mainbar,
.question,
.sidebar
{
	margin: 12px!important;
}

.users-sidebar .userDetails {
	margin: 1rem .5rem!important;
}

/*___ Paddings _____________________________________________________________________*/

#answers,
#mainArea,
#mainbar,
#sideBar,
#sidebar,
#tags_list .tag-cell,
.grid-layout>.grid-layout--cell,
.left-sidebar,
.mainbar,
.message.message-info,
.module,
.question,
.sidebar,
.wmx4
{
	padding: 12px!important;
}

selector {
	padding: 6px!important;
}

/*___ Heights ______________________________________________________________________*/

selector {
	max-height: none!important;
}

#tags_list .excerpt {
	height: auto!important;
}

/*___ Widths _______________________________________________________________________*/

#content,
body>.container
{
	max-width: none!important;
}

selector {
	width: auto!important;
}


.users-sidebar,
.featured-site .site-details {
    width: 93%!important;
}

#content,
.contentWrapper,
.question-container
{
	width: 98%!important;
}

.question.question-hot {
    width: 92%!important;
}

#mainArea,
#mainbar,
.mainbar
{
    width: 74%!important;
}

.badges-page .badge-description {
    width: 73%!important;
}

.privileges-page .short-description {
    width: 25%!important;
}

#sideBar,
#sidebar,
.sidebar
{
    width: 20%!important;
}

.badges-page .badge-cell-large {
    width: 17%!important;
}

.badges-page .badge-count,
.left-sidebar {
    width: 9%!important;
}



}

@-moz-document domain("stackexchange.com") {
/*__________________________________________________________________________________*/
a[href],
td span {
	color: #0ff!important;
	text-shadow: 1px 1px #000!important;
}

a[href]:visited {
	color: #fc79fc!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

.fc-black-500,
.item-multiplier-count,
.page-numbers.current,
.reputation-score,
.s-btn__muted.s-btn__outlined.is-selected,
.s-btn__muted.s-btn__outlined:focus,
.vote-count-post strong,
a[href]:hover span,
a[href]:hover,
h1,
input[type="checkbox"]:checked+label,
input[type="radio"]:checked+label {
	color: #ff0!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

#tags_list .excerpt,
.answer-votes,
.badgecount,
.excerpt,
.fc-medium,
.featured-site .site-details,
.hero-box .column,
.label-key b,
.label-key strong,
.metaInfo,
.mini-counts.warm,
.module.community-bulletin .bulletin-title,
.narrow .mini-counts,
.narrow .status,
.page-numbers,
.post-text blockquote p,
.post-text p,
.statscontainer,
.status,
.topbar-dialog .btn-secondary,
.user-info,
.votes,
.warm,
body,
code,
li,
p
{
	color: #fff!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Khaki ________________________________________________________________________*/

.comment-score span,
.heading,
.hero-box em,
.module h3,
.privileges-page .short-description,
.s-sidebarwidget--header,
.subheader h1,
h1,
h2 {
	color: #F0E890!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Burlywood ____________________________________________________________________*/

.narrow .views,
.narrow .votes,
.users-sidebar .userDetails .sb-siteName,
h3,
h4 {
	color: #e0b880!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Sandy Brown __________________________________________________________________*/

.featured-site .stats-label,
.label-key,
h5,
h6,
label {
	color: #F0A060!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Blue _________________________________________________________________________*/

.relativetime-clean,
.relativetime,
time {
	color: #07beff!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Green ________________________________________________________________________*/


selector {
	color: #07ff24!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*___ Red __________________________________________________________________________*/

.-ignore,
.status.unanswered strong {
	color: #ff0707!important;
	font-weight: 700!important;
	text-shadow: 1px 1px #000!important;
}

/*__________________________________________________________________________________*/

input,
input[type=email],
input[type=number],
input[type=password],
input[type=search],
input[type=tel],
input[type=text],
select,
textarea {
	background-color: #1d0909!important;
	border-radius: 22px!important;
	color: #fff!important;
}

/*__________________________________________________________________________________*/

::selection {
	background: #ff9!important;
	color: #000!important;
}

pre ::selection {
	background: #ff9!important;
	color: #fff!important;
}

::placeholder { /* Chrome, Firefox, Opera, Safari 10.1+ */
  color: #f48f8f;
  opacity: 1; /* Firefox */
}

/*___ Code _________________________________________________________________________*/

.status.answered, .status.answered .mini-counts, .status.answered strong {
    color: #71ffa0!important;
}

.pun {
    color: #fff!important;
}

.pln {
    color: #ffe7a0!important;
}

.kwd, .dec {
    color: #adadff!important;
}

.str, .lit, .tag {
    color: #ff8c8c!important;
}

.com {
    color: #c0c0c0!important;
}

.typ {
    color: #45d5ff!important;
}













}

@-moz-document domain("stackexchange.com") {
/*__________________________________________________________________________________*/

body,
html {
	background: url(https://image.ibb.co/hVmiOe/wallpaper_with_black_background_45_free_wallpaper.jpg)!important;
	background-attachment: fixed!important;
}

.s-hero__dark {
	background-image: none!important;
}

/*___ Background Gray ______________________________________________________________*/

.s-hero__dark,
.site-footer,
.site-header,
.top-bar,
header.siteHeader
{
	background-color: #060505!important;
}

/*___ Background 0000 ______________________________________________________________*/

.page-numbers,
.s-sidebarwidget--header
{
	background: rgba(0, 0, 0, 0)!important;
}

/*___ Background-color 0000 ________________________________________________________*/

#content,
#tabs a,
.badge,
.badge-tag,
.page-numbers,
.post-tag,
.tabs a,
.top-bar .-secondary .-ctas .btn-topbar-clear:focus,
.top-bar .-secondary .-ctas .btn-topbar-clear:hover,
.top-bar .-secondary .-item .-link:hover,
.top-bar a.-logo:hover,
.topbar-dialog .header
{
	background-color: rgba(0, 0, 0, 0)!important;
}

/*___ Highlight Background _________________________________________________________*/

selector:hover {
	border-left-color: #434343!important;
	background: linear-gradient(to right, #383838, #0000)!important;
}

code,
pre {
	background: #111!important;
	background: url(https://image.ibb.co/e8fWZk/red_carbon_pattern_by_mystica_264_d4ceu9m.png) #111!important;
	background: url(https://image.ibb.co/msdy5U/Red_Fabric.jpg) #111!important;
}

/*___ Highlight Background _________________________________________________________*/

selector:hover {
	background: linear-gradient(to right, #383838, #0000)!important;
	border-left-color: #434343!important;
}

/*__ Blue __________________________________________________________________________*/

.btn,
.btn-secondary,
.button,
.hero-box.double-panel .panel.white .btn,
.top-bar .-secondary .-ctas .btn-topbar-primary,
a.button,
button:not(.s-btn),
input[type="button"]:not(.s-btn),
input[type="submit"]:not(.s-btn)
{
	background-image: linear-gradient(to bottom right, #043dda, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

.btn:hover,
.btn-secondary:hover,
.button:hover,
.hero-box.double-panel .panel.white .btn:hover,
.top-bar .-secondary .-ctas .btn-topbar-primary:hover,
a.button:hover,
button:not(.s-btn):hover,
input[type="button"]:not(.s-btn):hover,
input[type="submit"]:not(.s-btn):hover
{
	background-image: linear-gradient(to bottom right, #154eeb, #000)!important;
	color: #ff0!important;
}

.btn:active,
.btn-secondary:active,
.button:active,
.hero-box.double-panel .panel.white .btn:active,
.top-bar .-secondary .-ctas .btn-topbar-primary:active,
a.button:active,
button:not(.s-btn):active,
input[type="button"]:not(.s-btn):active,
input[type="submit"]:not(.s-btn):active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Red __________________________________________________________________________*/

selector {
	background-image: linear-gradient(to bottom right, #f00, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
}

selector:hover {
	background-image: linear-gradient(to bottom right, #f11, #000)!important;
	color: #ff0!important;
}

selector:active {
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
}

/*__ Orange ________________________________________________________________________*/

.bg-black-050,
.btn,
.s-btn__filled,
input[type=button],
input[type=submit]
{
	background-image: linear-gradient(to bottom right, #da3a04, #000)!important;
	border-radius: 10px!important;
	border: none!important;
	box-shadow: 1px 1px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
	color: #fff!important;
