#add-login-page .major-provider.facebook-login:active,
#login-page .major-provider.facebook-login:active,
#signup-page .major-provider.facebook-login:active,
.facebook-login:active
{
	box-shadow: inset 2px 2px 1px black, inset 2px 3px 5px rgba(0, 0, 0, 0.3), inset -2px -3px 5px rgba(255, 255, 255, 0.5)!important;
