#login-page #formContainer:hover,
#signup-page #formContainer:hover,
#tags_list .tag-cellh,
.grid-layout>.grid-layout--cell:hover,
.module:hover,
.popup:hover,
.tag-popup .-container:hover,
.topbar-dialog:hover,
.topbar-dialog.network-logo-dialog .dialog-content:hover,
.wmx4:hover
{
	box-shadow: inset 0 0 5px rgba(0, 0, 0, .4), rgba(155, 155, 155, 1) 0 1px 0, 10px 10px 5px rgba(10, 10, 10, .4), rgba(155, 155, 155, 1) 0 1px 0!important;
