.answered-accepted,
.s-btn__muted.s-btn__outlined.is-selected,
.show-votes .sidebar-linked .spacer>a:first-child .answer-votes.answered-accepted,
.show-votes .sidebar-related .spacer>a:first-child .answer-votes.answered-accepted,
