@-moz-document domain("stackexchange.com") {
/*__________________________________________________________________________________*/

#tags_list .tag-cell,
.badges-page .general .badge-hierarchy,
.grid-layout>.grid-layout--cell,
.grid__allcells4>.grid,
.grid__allcells4>.grid--cell,
.hero-box .column,
.question-container,
.question-summary,
.users-sidebar .userDetails,
ul.comments-list .comment *
{
	filter: brightness(75%)!important;
}

/*__________________________________________________________________________________*/

#tags_list .tag-cell:hover,
.badges-page .general .badge-hierarchy:hover,
.grid-layout>.grid-layout--cell:hover,
.grid__allcells4>.grid--cell:hover,
.grid__allcells4>.grid:hover,
.hero-box .column:hover,
.question-container:hover,
.question-summary:hover,
.users-sidebar .userDetails:hover,
ul.comments-list .comment:hover *
{
	filter: brightness(100%)!important;
}

/*__________________________________________________________________________________*/

img {
	border-radius: 22px!important;
}

/*__________________________________________________________________________________*/

img {
	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/


li,
p,
tr {
	filter: brightness(68%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

blockquote p,
li:hover,
p:hover,
selector:hover p,
tr:hover {
	filter: brightness(100%)!important;
	transition: color .125s ease-in, color .5s ease-out, filter .5s ease-in, filter .8s ease-out, box-shadow .125s ease-in, box-shadow .5s ease-out!important;
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

selector:hover {
	opacity: 1!important;
}

}

