css:
@-moz-document url-prefix("https://quizlet.com/") {
/*main page (latest)*/
.DashboardHeader {
	background: #1e1e1e;
}
.UIContainer {
	background: #1e1e1e;
}
.DashboardPage {
	background: #1e1e1e;
}
.DashboardPage-header {
	background: #1e1e1e;
}
.DashboardLayout-sidebar {
	background: #1e1e1e;
}

/*My study sets*/
.DashboardLayout-main {
	background: #1e1e1e;
}
.UIToggle {
	border: none!important;
}
.UIDropdown {
	border: none!important;
}

/*premium*/
.PrismicLinkGroup {
    background: #1e1e1e;
}
.SiteFooter {
	background: #1e1e1e;
}

/*help center*/
.container {
	background: #1e1e1e;
}

/*create*/
.UIModalHeader {
    background-color: #1e1e1e;
    color: #fff;
}
.UIModalBody {
    background: #1e1e1e;
}
.UIFloatedCardBackdrop-bottom {
    background: #1e1e1e;
}
.CreateSetDiagramUploader-banner {
	border: none!important;
    background: #1e1e1e;
}
.CreateSetHeader {
    background: #1e1e1e;
}

/*flashcards*/
.has-adz .SetPage-headerWrapper {
    background: #1e1e1e;
}
.SetPageHeader {
    background: #1e1e1e;
}
.SetPage.has-adz .SetPage-options {
	background: #1e1e1e;
}
.SetPage-modesWrapper {
	background: #1e1e1e;
}
.SetPage-modes {
	background: #1e1e1e;
}
.SetPage-setAdzContainer {
	background: #1e1e1e;
}
.SetPage-terms {
	background: #1e1e1e;
}
.SetPage-diagram, .SetPage-terms, .SetPage-recommendedSets {
	background: #1e1e1e;
}
.SetPageWrapper {
	background: #1e1e1e;
}
.ModeControls {
	background: #1e1e1e;
}
.ModeControls-actions {
	background: #1e1e1e;
}
}
