	-webkit-box-reflect: below 0 -webkit-gradient(linear, left top, left bottom, from(transparent), color-stop(70%, transparent), to(rgba(250, 250, 250, 0.1)));
}

/*__________________________________________________________________________________*/

selector {
	opacity: .8!important;
}

.common__overlay___1zAoY:hover,
selector:hover {
	opacity: 1!important;
}
}

@-moz-document domain("tunein.com") {
#playerVolume svg {
    background: url(https://i.ibb.co/xSgXWZN/volume-36x36.png)!important;
	box-sizing: border-box!important;
    width: 32px!important;
    height: 32px!important;
    padding-left: 32px!important;
}

#playerShare svg {
    background: url(https://i.ibb.co/r5N57tK/share-32x32.png)!important;
	box-sizing: border-box!important;
    width: 32px!important;
    height: 32px!important;
    padding-left: 32px!important;
}

#playerFavorite svg[data-testid="playerFavoriteIconNotFavorite"] {
    background: url(https://i.ibb.co/cD452WP/healthy-hear-darkt-32x32.png)!important;
	box-sizing: border-box!important;
    width: 32px!important;
    height: 30px!important;
    padding-left: 32px!important;
}

#playerFavorite svg[data-testid="playerFavoriteIconDidFavorite"] {
    background: url(https://i.ibb.co/0DVFg1R/heart-32x32.png)!important;
	box-sizing: border-box!important;
    width: 32px!important;
    height: 30px!important;
    padding-left: 32px!important;
}

/* Small Hearts _____________________________________________________________*/

.icons__icon-heart-empty___3oRWQ {
    background: url(https://i.ibb.co/vhC7kgD/healthy-hear-darkt-14x14.png)!important;
	box-sizing: border-box!important;
    width: 14px!important;
    height: 13px!important;
    padding-left: 14px!important;
}

.icons__icon-heart-full___3aZQf {
    background: url(https://i.ibb.co/TYRgM1j/healthy-heart-14x14.png)!important;
	box-sizing: border-box!important;
    width: 14px!important;
    height: 13px!important;
    padding-left: 14px!important;
}

.icons__icon-heart-full___3aZQf:before,
.icons__icon-heart-empty___3oRWQ:before {
    content: ""!important;
}

