@-moz-document url("https://duelingnexus.com/login"), url("https://duelingnexus.com/register"), url("https://duelingnexus.com/recover"), url("https://duelingnexus.com/home"), url("https://duelingnexus.com/duel"), url("https://duelingnexus.com/gamelist"), url("https://duelingnexus.com/hostgame"), url("https://duelingnexus.com/decks"), url("https://duelingnexus.com/profile"), url("https://duelingnexus.com/leaderboard"), url("https://duelingnexus.com/donate") {
/*SECTION 01 NAVGLOBAL*/
/*This section is global code that applies to all of the navigation pages. For global code for the game and deckbuilding pages, go to GAMEGLOBAL*/
html {
    background-position: right bottom
}
@media (orientation: landscape) {
body {
    font-size: 2vh;
    line-height: 1;
}
h2, h3 {/*This is the main header for each of the tabs.*/
    font-variant: small-caps;
    font-size: 3.5vh;
    line-height: 1;
    text-transform: none;
    letter-spacing: .1vw;
    -moz-user-select: none;
    -o-user-select: none;
    user-select: none;
    margin: .5vh;
    padding: 0;
}
div#app {
    margin-bottom: 40px /*Increase this value if your taskbar blocks the interface.*/
}
#socket-connection {/*This keeps the socket on the top left*/
    line-height: 1.5;
    background-color: rgba(0, 0, 0, 0.5);
    border: none;
    height: 3vh;
    z-index: 3;
    position: fixed;
    top: 12.5vh;
    left: 1vw;
}
/*Here we have some general clicky thingy rules.*/
a {
    -webkit-transition: all 1s ease-in-out;
    -moz-transition: all 1s ease-in-out;
    -o-transition: all 1s ease-in-out;
    transition: all 1s ease-in-out;
    font-size: 2vh;
    padding: 0;
    text-decoration: underline;
    opacity: .75;
}
a:focus {
    font-style: italic
}
a:hover {
    opacity: 1
}
article button, .fake-button[data-v-6f705b54], .file-picker-container img {  
    font-size: 2vh;
    line-height: 1;
    text-align: center;
}
p button, #gamelist-buttons button, .button, button[data-v-6f705b54], .fake-button[data-v-6f705b54], button[data-v-7d3c70a7], .fake-button[data-v-7d3c70a7] {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    transition-property: color, background, border;
    font-weight: bold;
    border: .15vw solid;
    min-height: 0;
    min-width: 0;
}
p button:focus, #gamelist-buttons button:focus, .button:focus, button[data-v-6f705b54]:focus, .fake-button[data-v-6f705b54]:focus, button[data-v-7d3c70a7]:focus, .fake-button[data-v-7d3c70a7]:focus {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    transition-property: color, background, border;
    font-style: italic;
}
p button:hover, #gamelist-buttons button:hover, .button:hover, button[data-v-6f705b54]:hover, .fake-button[data-v-6f705b54]:hover, button[data-v-7d3c70a7]:hover, .fake-button[data-v-7d3c70a7]:hover {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    transition-property: color, background, border;
}
article button:focus, p button:focus, .file-picker-container:focus {
    -moz-transition-duration: 2s;
    -o-transition-duration: 2s;
    transition-duration: 2s;
    transition-property: color, background, border;
    font-style: italic;
}
hr {
    display: none
}
form div, form input[type="checkbox"] {
    margin: 0
}
form input[type="email"], form input[type="password"], form input[type="text"], input[type="text"], #login-submit, #register-area button, #recover-submit {
    -webkit-transition-duration: 1s;
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    -webkit-transition-property: all;
    -moz-transition-property: all;
    -o-transition-property: all;
    transition-property: all;
    font-family: sans-serif;
    text-indent: 0.5vw;
    border-width: .15vw;
    height: 2.75vw;
    padding: 0;
}
form input[type="email"]:focus, form input[type="password"]:focus, form input[type="text"]:focus, input[type="text"]:focus, #login-submit:focus, #register-area button:focus, #recover-submit:focus, form input[type="email"]:hover, form input[type="password"]:hover, form input[type="text"]:hover, input[type="text"]:hover, #login-submit:hover, #register-area button:hover, #recover-submit:hover {
    -webkit-transition-duration: 1s;
    -moz-transition-duration: 1s;
    -o-transition-duration: 1s;
    transition-duration: 1s;
    -webkit-transition-property: all;
    -moz-transition-property: all;
    -o-transition-property: all;
    transition-property: all;
}
input[type="checkbox"], label {
    vertical-align: middle
}
input, label {
    font-size: 2vh;
    opacity: .7;
    cursor: pointer;
}
input:hover, label:hover {
    opacity: 1
}
/*This is the code for the navbar, which can be seen from any tab.*/
#navbar {
    background-color: black;
    height: 8vh;
    min-height: 0;
    max-height: 25vh;
    width: auto;
    z-index: 3;
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
}
#navbar button {
    border: 0;
    padding: 0;
}
#navbar button img, p button img {
    height: 3.25vh;
    width: 3.25vh;
    padding-bottom: .5vh;
}
#navbar-left button, #navbar-middle button, #navbar-right button {
    height: 8vh;
    min-height: 0;
    min-width: 0;
    margin: 0;
}
#navbar-middle button, #navbar-right button {
    font-size: 3.5vh;
    margin-right: 1vh;
}
#navbar-left button {
    width: 8vh;
    float: left;
}
#navbar-left br, #navbar-middle br {
    display: none
}
#navbar-left {
    width: 24vh
}
#navbar-middle {
    width: auto;
    position: fixed;
    left: 25vh;
    right: 25vw;
}
#navbar-right {
    float: right
}
#chat-button {
    font-size: 0
}
article {
    margin-top: 9vh
}
/*Table stuff*/
td, .table-button:active {
    line-height: 2.5;
	margin: 0;
	padding: .5vw;
}
.table-button:active {
    font-style: italic
}
}
@media (orientation: portrait) {
html {
    background-position: right bottom;
    background-size: 375% 100%;
}
#app:before {
    content: " ";
    background: url(https://cdn.discordapp.com/attachments/403735097498664960/616381523796426759/information-icon-transparent_1916042.png) 3vh/3vh;
    display: block;
    height: 3vh;
    width: 3vh;
    transform: translateY(4vh) translateX(1vh)
}
#app:after {
    content: "Please hold your device horizontally to use the website. You can still hold your device vertically to type.";
    font-size: 3vh;
    text-align: justify;
    text-indent: 4vh;
    color: #F33;
    background-color: rgba(0, 0, 0, 0.8);
    height: auto;
    width: 85%;
    display: block;
    padding: 1vh;
}
}
}

@-moz-document url("https://duelingnexus.com/login"), url("https://duelingnexus.com/register"), url("https://duelingnexus.com/recover") {
/*SECTION 02 LOGIN*/
/*This section applies to the Login, Register, and Recover pages, but not the news.*/
@media (orientation: landscape) {
#login-area[data-v-9bfe76b0], #register-area[data-v-4068c77b], #recover-area[data-v-8098b0b4] {
