    width: 120vh;
    float: none;
}
#login-area[data-v-9bfe76b0], #recover-area[data-v-80980b4] {
    position: relative
}
#register-area[data-v-4068c77b] {
    overflow-y: auto
}
#login-area h2::before, #login-email, #login-area h2::after, #login-password, .input-checkbox-group, #login-submit, #login-area[data-v-9bfe76b0] p:not(#links-area), #links-area {/*General code for this page. Ordered from top to bottom, and repeated below.*/
    font-size: 1.5vw;
    position: absolute;
}
#login-area h2::before {
    content: "Email :";
    top: 16%;
    left: 2%;
}
#login-email {
    width: 98%;
    top: 21%;
    left: 1%
}
#login-area h2::after {
    content: "Password :";
    top: 32%;
    left: 2%;
}
#login-password {
    width: 98%;
    top: 37%;
    left: 1%;
}
.input-checkbox-group {
    top: 45%;
    left: 35%;
}
#login-submit, #register-area button, #recover-submit {
    font-size: 2.5vh;
    line-height: 1.5;
    border-width: .15vw;
    height: auto;
    min-height: 0;
    width: 98%;
    right: 1%;
    padding: 0;
    left: 1%;
}
#login-submit {
    top: 50%
}
#register-area button {
    margin-top: 2vh
}
#login-area[data-v-9bfe76b0] p:not(#links-area), #register-area[data-v-4068c77b] p:not(#links-area) {
    font-size: 2vh;
    margin: 0;
}
#login-area[data-v-9bfe76b0] p:not(#links-area) {
    top: 57%
}
.login-error p[data-v-9bfe76b0] {
    line-height: 3.5
}
#login-email, #login-password, #register-email, #register-username, #register-password, #register-password2, #recover-email {
    border-width: .3vw;
    border-style: solid;
}
#links-area[data-v-9bfe76b0], #links-area[data-v-4068c77b], #links-area[data-v-80980b4] {
    font-size: 0;
    border: 0;
    margin: 0;
    padding: 0;
    width: 100%;
    left: 0;
}
#links-area[data-v-9bfe76b0], #links-area[data-v-80980b4] {
    bottom: 10%
}
#links-area[data-v-9bfe76b0] a, #links-area[data-v-4068c77b] a, #links-area[data-v-80980b4] a {
    line-height: 6;
    width: 50%;
    float: left;
}
#links-area[data-v-4068c77b] a, #links-area[data-v-80980b4] a {
    margin: 0 25%
}
#links-area[data-v-9bfe76b0] a {
    border: 0
}
#register-email, #register-username, #register-password, #register-password2, #recover-email {
    margin: 1vh 0 3vh
}
#recover-area::before {
    content: "This feature is currently unavailable.";
    color: #F33;
}
}
@media (orientation: portrait) {
div:not(#app) {
    display: none
}
}
}

@-moz-document url("https://duelingnexus.com/login"), url("https://duelingnexus.com/home") {
/*SECTION 03 NEWS*/
/*This section applies to the News Area on both the login and home pages, as well as any transitions between the two.*/
@media (orientation: landscape) {
#news-area {
    height: 4vh;
    width: auto;
    max-width: 100vw;
    overflow: hidden;
    margin: 0;
    padding: .5vh;
    position: absolute;
    bottom: 15vh;
    right: 1vw;
    left: 1vw;
}
#news-area:hover {
    height: 16vh;
    overflow-y: auto;
}
#news-area strong {
    font-size: 2vh;
    line-height: 1;
    margin: 1vw;
}
#news-area p {
    font-size: 2vh;
    line-height: 1;
    margin: 2vw;
}
#news-area::after {
    content: "J×Y v3.0 by Yubelious!";
    font-size: 0;
    text-align: right;
    display: block;
    margin: 1.5vw;
    position: relative;
}
#news-area:hover::after {
    font-size: .1vw
}
}
@media (orientation: portrait) {
div:not(#app) {
    display: none
}
}
}

@-moz-document url("https://duelingnexus.com/home") {
/*SECTION 04 HOME*/
/*This section affects the home page.*/
@media (orientation: landscape) {
#navbar-middle button:first-of-type {
    animation: thistab 2s;
    font-size: 0;
    width: 8vh;
    vertical-align: top;
}
}
@media (orientation: portrait) {
div:not(#app) {
    display: none
}
}
@keyframes thistab {
    from {
        font-size: 3vh;
